package com.BasicProgram;

public class EvenOrOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
             int n=21;
             if(n/2==0)
             {
            	 System.out.println("Even");
             }
             else 
             {
            	 System.out.println("Odd");
             }
	}

}
